import React, { useState } from 'react';
import { useSelector } from 'react-redux/es/hooks/useSelector';
import { useDispatch } from 'react-redux'
import { v4 as uuid } from 'uuid'
import { ADD_TODO } from '../../../store/todo-actions'

function TestState() {

  const [inputValue, setInputValue] = useState('');

  const items = useSelector((state) => state.todos)
  const doneItems = items.filter((item) => item.done)
  const todoItems = items.filter((item) => !item.done)


  const dispatch = useDispatch()


  const handleAddFunction = (e) => {
    setInputValue(e.target.value);
    console.log(inputValue)
  }

  const handleAdd = () => {
    dispatch({
      type: ADD_TODO,
      payload: {
        id: uuid(),
        text: inputValue,
        done: false,
      },
    })
  }

  return (
    <div className="App">
        <div className="doneItems py-4">
            {doneItems.map((item) => (
          <div key={item.id}>
             Виконані:{item.text} <button onClick={handleAddFunction}>Add</button>
          </div>
          
        ))}
        </div>
        <div className="doneItems py-4">
            {todoItems.map((item) => (
          <div key={item.id}>
            Невиконані: {item.text}
          </div>
          
            ))}
        </div>
        <div className='inputToDo'>
              <input  
                      className='inputSettings bg-slate-500'
                      type="text"
                      value={inputValue}
                      onChange={handleAddFunction}>
              </input>
        </div>
    </div>
  );
}

export default TestState;
