import React, { useState } from 'react';
import { useSelector } from 'react-redux/es/hooks/useSelector';
import { useDispatch } from 'react-redux'
import { v4 as uuid } from 'uuid'
import { ADD_TODO, DELETE_TODO, UPDATE_TODO } from '../../../store/todo-actions'

function AllToDo() {

  const [inputValue, setInputValue] = useState('');

  const items = useSelector((state) => state.todos)
  const doneItems = items.filter((item) => item.done)
  const todoItems = items.filter((item) => !item.done)


  const dispatch = useDispatch()


  const handleAddFunction = (e) => {
    setInputValue(e.target.value);
    console.log(inputValue)
  }

  
  const handleAdd = () => {
      dispatch({
        type: ADD_TODO,
        payload: {
          id: uuid(),
          text: inputValue,
          done: false,
        },
      })
  }

  const handleUPDATE = (item, done) => {
    dispatch({
      type: UPDATE_TODO,
      payload: {
        id: item.id,
        done,
      },
    })
}

  const handleDELETE = (item) => {
    dispatch({
      type: DELETE_TODO,
      payload: {
        id: item.id,
      },
    })
}
 
    

  return (
    <div className="App py-12 bg-red-500 rounded-3xl">
      <div className="App_container flex justify-around">
      <div className="doneItems py-4 border-2 border-red-100">
        Виконані завдання:
            {doneItems.map((item) => (
          <div key={item.id}>
             {item.text} 
             <button className="delete py-4" onClick={() => handleDELETE(item)}>DELETE</button>
          </div>
          
        ))}
        </div>
        <div className="doneItems py-4 border-2 border-red-100">
        Невиконані завдання:
            {todoItems.map((item) => (
          <div key={item.id}>
            <input type='checkbox' checked={item.done} onChange={() => handleUPDATE(item, true)}></input>
             {item.text}
            <button className="delete py-4" onClick={() => handleDELETE(item)}>DELETE</button>
          </div>
          
            ))}
        </div>
        <div className='inputToDo border-2 border-red-100'>
              <input  
                      className='inputSettings'
                      type="text"
                      value={inputValue}
                      onChange={handleAddFunction}>
              </input>
              <button className="addButton" onClick={handleAdd}>ADD</button>
        </div>
      </div>
    </div>
  );
}

export default AllToDo;
