import React, { useState } from 'react';
import { useSelector } from 'react-redux/es/hooks/useSelector';
import { useDispatch } from 'react-redux'
import { v4 as uuid } from 'uuid'
import { ADD_TODO, UPDATE_TODO } from '../../../store/todo-actions'

const Todos = () => {
    const [inputValue, setInputValue] = useState('');

    const items = useSelector((state) => state.todos)
    const todoItems = items.filter((item) => !item.done)
  
  
    const dispatch = useDispatch()
  
  
    const handleAddFunction = (e) => {
      setInputValue(e.target.value);
      console.log(inputValue)
    }
  
    
    const handleAdd = () => {
        dispatch({
          type: ADD_TODO,
          payload: {
            id: uuid(),
            text: inputValue,
            done: false,
          },
        })
    }
  
    const handleUPDATE = (item, done) => {
      dispatch({
        type: UPDATE_TODO,
        payload: {
          id: item.id,
          done,
        },
      })
  }
  
   
    return (
      <div className="App bg-gray-400">
          <div className="todoItems py-4">
              {todoItems.map((item) => (
            <div key={item.id}>
              <input type='checkbox' checked={item.done} onChange={() => handleUPDATE(item, true)}></input>
              Невиконані: {item.text}
            </div>
            
              ))}
          </div>
          <div className='inputToDo'>
                <input  
                        className='inputSettings bg-slate-500'
                        type="text"
                        value={inputValue}
                        onChange={handleAddFunction}>
                </input>
                <button className="addButton" onClick={handleAdd}>ADD</button>
          </div>
      </div>
    );
}

export default Todos;
