import React, { useState } from 'react';
import { useSelector } from 'react-redux/es/hooks/useSelector';
import { useDispatch } from 'react-redux'
import { v4 as uuid } from 'uuid'
import { ADD_TODO, DELETE_TODO, UPDATE_TODO } from '../../../store/todo-actions'

function TestState() {

  const [inputValue, setInputValue] = useState('');

  const items = useSelector((state) => state.todos)
  const doneItems = items.filter((item) => item.done)
  const todoItems = items.filter((item) => !item.done)


  const dispatch = useDispatch()


  const handleAddFunction = (e) => {
    setInputValue(e.target.value);
    console.log(inputValue)
  }

  
  const handleAdd = () => {
      dispatch({
        type: ADD_TODO,
        payload: {
          id: uuid(),
          text: inputValue,
          done: false,
        },
      })
  }

  const handleUPDATE = (item, done) => {
    dispatch({
      type: UPDATE_TODO,
      payload: {
        id: item.id,
        done,
      },
    })
}

  const handleDELETE = (item) => {
    dispatch({
      type: DELETE_TODO,
      payload: {
        id: item.id,
      },
    })
}
 
    

  return (
    <div className="App bg-gray-400">
        <div className="doneItems py-4">
            {doneItems.map((item) => (
          <div key={item.id}>
            <input type='checkbox' checked={item.done}></input>
             Виконані:{item.text} 
             <button className="delete py-4" onClick={() => handleDELETE(item)}>DELETE</button>
          </div>
          
        ))}
        </div>
        <div className="doneItems py-4">
            {todoItems.map((item) => (
          <div key={item.id}>
            <input type='checkbox' checked={item.done} onChange={() => handleUPDATE(item, true)}></input>
            Невиконані: {item.text}
            <button className="delete py-4" onClick={() => handleDELETE(item)}>DELETE</button>
          </div>
          
            ))}
        </div>
        <div className='inputToDo'>
              <input  
                      className='inputSettings bg-slate-500'
                      type="text"
                      value={inputValue}
                      onChange={handleAddFunction}>
              </input>
              <button className="addButton" onClick={handleAdd}>ADD</button>
        </div>
    </div>
  );
}

export default TestState;
